import React, { Fragment } from 'react';
import logo from './logo.svg';
import './App.css';
import Navbar from './Navbar'
import LoginForm from './LoginForm'
import UserHeader from './UserHeader'
import Container from './Container'
import Cart from './Cart'
import Card from './Card'
import Search from './Search';

import { Route, Switch, Redirect } from 'react-router-dom'


class App extends React.Component {

  state = {
    loading: true,
    products: [],
    filteredProducts: [],
    loggedin: false,
    username: "",
    cart: [],
    search: "",
  }

  componentDidMount() {
    fetch("http://localhost:3000/api/v1/products")
      .then(res => res.json())
      .then(data => {
        this.setState({
          products: data,
          loading: false,
        })
      })
  }

  clickSearch = event => {
    const filteredProducts = this.state.products.filter(product => product.name.toLowerCase().includes(this.state.search.toLowerCase()))
    this.setState({
      filteredProducts: filteredProducts
    })
  }

  changeEvent = event => {
  this.setState({
    search: event.target.value
  })
  // console.log(event.target.value)
}

  addToCart = (itemObj) => {
    this.setState({
      cart: [itemObj, ...this.state.cart]
    })
  }

  removeFromCart = (itemObj) => {
    this.setState({
      cart: this.state.cart.filter(item => item.id !== itemObj.id)
    })
  }


  handleChange = (event) => {
      this.setState({
        // [event.target.name]: event.target.value
        username: event.target.value
      })
    }

  handleSubmit = () => {
    this.setState({
      loggedin: !this.state.loggedin
    })
  }


render() {
  // console.log(this.state.cart)
  // console.log(this.state.search)

  if (this.state.loading) {
      return <h1>ðŸŒº ...IT'S LOADING ... ðŸŒº</h1>
    } else {
  return (

    <div>
  <Navbar />
  <Switch>
    <Route path="/cart" render={(routerProps) => {
      return <Cart
        removeFromCart={this.removeFromCart}
        {...routerProps}
        products={this.state.cart}/>
    }}/>

    <Route path="/home" render={(routerProps) => {
      let allItems = []

      this.state.products.forEach(product => {
        allItems = [...allItems, product]
      })

      return <Fragment>
        <Search changeEvent={this.changeEvent} search={this.state.search}/>
        <Container
          products={this.state.products.filter(product => product.name.toLowerCase().includes(this.state.search.toLowerCase()))} addToCart={this.addToCart}
        />
      </Fragment>

    }}/>

  </Switch>
</div>






      )
    }
  }


}

export default App;


