import React, { Fragment } from 'react';
import logo from './logo.svg';
import './App.css';
import Navbar from './Navbar'
import LoginForm from './LoginForm'
import UserHeader from './UserHeader'

import Container from './Container'
// import Card from './Card.js'

import { Route, Switch, Redirect } from 'react-router-dom'


class App extends React.Component {

  state = {
    loading: true,
    products: [],
    loggedin: false,
    username: "",
    cart: [],
  }

  componentDidMount() {
    fetch("http://localhost:3000/api/v1/products")
      .then(res => res.json())
      .then(data => {
        this.setState({
          products: data,
          loading: false,
        })
      })
  }

  addToCart = (itemObj) => {
    this.setState({
      cart: [itemObj, ...this.state.cart]
    })
  }

  removeFromCart = (itemObj) => {
    this.setState({
      cart: this.state.cart.filter(item => item.id !== itemObj.id)
    })
  }


  handleChange = (event) => {
      this.setState({
        // [event.target.name]: event.target.value
        username: event.target.value
      })
    }

  handleSubmit = () => {
    this.setState({
      loggedin: !this.state.loggedin
    })
  }


render() {
  return (
    <div>
      <span>  ðŸŒ¸ Welcome to Beautiful Beauty! ðŸŒ¸  </span>
      <div classusername="sidebar" align="center">
        {
          this.state.loggedin ? <Fragment> <UserHeader loggedin={this.state.loggedin} username={this.state.username} handleSubmit={this.handleSubmit} /> </Fragment>
          : <LoginForm loggedin={this.state.loggedin} changeLogIn={this.changeLogIn} username={this.state.username} handleChange={this.handleChange} handleSubmit={this.handleSubmit} />

        }
      </div>
    <Navbar />
    <Container products={this.state.products} />
    </div>
    )
  }


}

export default App;


